import React, { useRef, useEffect } from "react";

function LiveStream() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const faceMemoryRef = useRef({}); // Use ref to hold face data without re-rendering

  const canvasWidth = 640;
  const canvasHeight = 480;

  useEffect(() => {
    let intervalId;

    async function startCamera() {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
        await videoRef.current.play();

        intervalId = setInterval(async () => {
          if (!videoRef.current || !canvasRef.current) return;

          const video = videoRef.current;
          const canvas = canvasRef.current;
          const ctx = canvas.getContext("2d");

          // Draw current frame on canvas
          ctx.clearRect(0, 0, canvasWidth, canvasHeight);
          ctx.drawImage(video, 0, 0, canvasWidth, canvasHeight);

          // Get base64 image from canvas
          const dataUrl = canvas.toDataURL("image/jpeg");

          try {
            const response = await fetch("http://localhost:5000/recognize", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ image: dataUrl }),
            });

            const result = await response.json();

            // Update faceMemoryRef with new detections
            const now = Date.now();
            const faceMemory = faceMemoryRef.current;

            result.faces.forEach(({ top, right, bottom, left, name }) => {
              const key = `${left}_${top}_${right}_${bottom}`;
              faceMemory[key] = { name, coords: { top, right, bottom, left }, timestamp: now };
            });

            // Remove stale faces older than 3 seconds
            Object.keys(faceMemory).forEach((key) => {
              if (now - faceMemory[key].timestamp > 3000) {
                delete faceMemory[key];
              }
            });

            // Draw bounding boxes and names from stable faceMemory
            Object.values(faceMemory).forEach(({ name, coords }) => {
              const { top, right, bottom, left } = coords;
              ctx.strokeStyle = "#00FF00";
              ctx.lineWidth = 2;
              ctx.strokeRect(left, top, right - left, bottom - top);

              ctx.fillStyle = "#00FF00";
              ctx.font = "18px Arial";
              ctx.fillText(name, left, top > 20 ? top - 8 : bottom + 20);
            });
          } catch (error) {
            console.error("Error recognizing faces:", error);
          }
        }, 800); // every 800ms

      } catch (err) {
        console.error("Camera error:", err);
      }
    }

    startCamera();

    return () => {
      clearInterval(intervalId);
      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, []); // empty dependency array - run only once

  return (
    <div style={{ position: "relative", width: canvasWidth, height: canvasHeight }}>
      <video
        ref={videoRef}
        width={canvasWidth}
        height={canvasHeight}
        muted
        style={{ position: "absolute", top: 0, left: 0, zIndex: 1 }}
      />
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        style={{ position: "absolute", top: 0, left: 0, zIndex: 2 }}
      />
    </div>
  );
}

export default LiveStream;
