import React, { useRef, useState } from 'react';
import { FaHome, FaChartLine, FaUser, FaShoppingCart, FaBell } from 'react-icons/fa'; 
import './registerpage.css'; 
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { Link } from 'react-router-dom';





function Registerpage() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [fullName, setFullName] = useState('');
  const [imageData, setImageData] = useState(null);
  const [cameraActive, setCameraActive] = useState(false);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setCameraActive(true);
    } catch (err) {
      console.error('Error accessing camera: ', err);
    }
  };

  const captureImage = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0);
    const dataUrl = canvas.toDataURL('image/png');
    setImageData(dataUrl);
    stopCamera();
  };

  const stopCamera = () => {
    const stream = videoRef.current?.srcObject;
    stream?.getTracks().forEach(track => track.stop());
    setCameraActive(false);
  };

  const retakeImage = () => {
    setImageData(null);
    startCamera();
  };

  const registerFace = () => {
    if (!imageData || !fullName) {
      alert("Please enter name and capture a photo first.");
      return;
    }
    alert(`Registering face for ${fullName}`);
    // You can now send `imageData` and `fullName` to your backend.
  };














  return (
    <div className="app-container">
      {/* Sidebar Section */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Container</h2> 
        </div>

        <div className="sidebar-menu">
          <Link to="/" className="menu-item">
            <FaHome className="icon" />  
            <span>Register</span>
          </Link>
          <Link to="/sales" className="menu-item">
            <FaShoppingCart className="icon" />
            <span>Livestream</span>
          </Link>
          <Link to="/customer" className="menu-item">
            <FaUser className="icon" />
            <span>Q-A Assistant</span>
          </Link>
          
          <Link to="/catalog" className="menu-item">
            <FaChartLine className="icon" />
            <span>Database</span>
          </Link>
          
        </div>

        <div className="sidebar-footer">
          <span className="user-name">POORANI</span>
        </div>
      </div>





     <div className="main-content">
        <div className="registration-container">
          <h3>Face Registration</h3>
          <p>Register new faces for recognition system</p>

          <label>Full Name</label>
          <input
            type="text"
            placeholder="Enter full name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />

          <label>Capture Photo</label>
          {!imageData && !cameraActive && (
            <button className="start-camera-btn" onClick={startCamera}>üì∑ Start Camera</button>
          )}

          {cameraActive && (
            <div className="camera-container">
              <video ref={videoRef} autoPlay muted playsInline className="camera-feed" />
              <button className="capture-btn" onClick={captureImage}>üì∏ Capture Photo</button>
            </div>
          )}

          {imageData && (
            <div className="image-preview">
              <img src={imageData} alt="Captured face" />
              <button className="retake-btn" onClick={retakeImage}>üîÅ Retake Image</button>
            </div>
          )}

          <canvas ref={canvasRef} style={{ display: 'none' }} />

          <button className="register-btn" onClick={registerFace}>Register Face</button>
        </div>
      </div>


      
    </div>
  );
}

export default Registerpage;
