import React, { useRef, useEffect, useState } from "react";

function LiveStream() {
  const videoRef = useRef(null);
  const [recognizedFaces, setRecognizedFaces] = useState([]);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true }).then((stream) => {
      videoRef.current.srcObject = stream;
    });

    const interval = setInterval(() => {
      captureFrameAndRecognize();
    }, 3000); // capture every 3 seconds

    return () => clearInterval(interval);
  }, []);

  const captureFrameAndRecognize = () => {
    const video = videoRef.current;
    const canvas = document.createElement("canvas");
    canvas.width = video.videoWidth || 640;
    canvas.height = video.videoHeight || 480;
    canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);

    const dataUrl = canvas.toDataURL("image/jpeg");

    fetch("http://localhost:5000/recognize", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ image: dataUrl })
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.faces) {
          setRecognizedFaces(data.faces);
        }
      })
      .catch((err) => console.error("Recognition error", err));
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h2>🎥 Live Face Recognition with Flask</h2>
      <video ref={videoRef} autoPlay muted playsInline width="640" height="480" style={{ border: "1px solid black" }} />
      <div style={{ marginTop: "20px" }}>
        <h3>Recognized Faces:</h3>
        {recognizedFaces.length === 0 ? (
          <p>🔍 Scanning...</p>
        ) : (
          recognizedFaces.map((face, index) => (
            <p key={index}>
              🧠 <strong>{face.name}</strong> at ({face.left},{face.top})
            </p>
          ))
        )}
      </div>
    </div>
  );
}

export default  LiveStream;
