import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaHome, FaShoppingCart, FaUser, FaChartLine } from 'react-icons/fa';
import './assistant.css';

const Assistant = () => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');

  const handleAsk = async () => {
    if (!question.trim()) return;
    try {
      const response = await fetch('http://localhost:5000/query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });
      const data = await response.json();
      setAnswer(data.answer || 'No answer received.');
    } catch (error) {
      setAnswer('Error connecting to backend.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>FaceVerse AI</h2>
        </div>

        <div className="sidebar-menu">
          <Link to="/" className="menu-item"><FaHome className="icon" /><span>Home</span></Link>
          <Link to="/register" className="menu-item"><FaHome className="icon" /><span>Register</span></Link>
          <Link to="/livestream" className="menu-item"><FaShoppingCart className="icon" /><span>Livestream</span></Link>
          <Link to="/assistant" className="menu-item active"><FaUser className="icon" /><span>Q-A Assistant</span></Link>
          <Link to="/databasepage" className="menu-item"><FaChartLine className="icon" /><span>Database</span></Link>
        </div>

        <div className="sidebar-footer">
          <span className="user-name">POORANI</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="faq-section">
          <h2>🧠 What can you ask?</h2>
          <ul className="faq-list">
            <li>🔢 <strong>Total records:</strong> "How many faces are registered?"</li>
            <li>🔤 <strong>Names by alphabet:</strong> "Names starting with A"</li>
            <li>🕒 <strong>Person's registration time:</strong> "When did John register?"</li>
            <li>📆 <strong>Registrations on a specific date:</strong> "Registrations on 2025-05-27"</li>
            <li>📋 <strong>All registered names:</strong> "Show all registered names"</li>
            <li>🎯 <strong>First or recent registered person:</strong> "Who was the first person registered?"</li>
            <li>🔍 <strong>Search by partial name:</strong> "Find names containing 'raj'"</li>
            <li>📅 <strong>Today's registrations:</strong> "How many were registered today?"</li>
            <li>⏰ <strong>All registration times for a person:</strong> "List all times Poorani was registered"</li>
            <li>🤖 <strong>Other help:</strong> "How do I register a face?" or general queries</li>
          </ul>
        </div>

        <div className="input-group">
          <input
            type="text"
            placeholder="Ask your question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleAsk()}
          />
          <button onClick={handleAsk}>Ask</button>
        </div>

        {answer && <div className="answer"><strong>Answer:</strong> {answer}</div>}
      </div>
    </div>
  );
};

export default Assistant;
